mapper.update:
update stu_student
set sname = #{sname,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=CHAR},
  birth = #{birth,jdbcType=DATE},
  photo_url = #{photoUrl,jdbcType=VARCHAR}
where sno = #{sno,jdbcType=INTEGER}

mapper.select:
select sno, sname, gender, birth, photo_url
from stu_student
where sno = #{sno,jdbcType=INTEGER}
